    /**
     * Install the SurveyManager application.
     *
     * @return boolean True on success, or false.
     */
    public function install()
    {
        // create all tables from according entity definitions
        try {
            DoctrineHelper::createSchema($this->entityManager, $this->listEntityClasses());
        } catch (\Exception $e) {
            if (System::isDevelopmentMode()) {
                return LogUtil::registerError($this->__('Doctrine Exception: ') . $e->getMessage());
            }
            $returnMessage = $this->__f('An error was encountered while creating the tables for the %s extension.', array($this->name));
            if (!System::isDevelopmentMode()) {
                $returnMessage .= ' ' . $this->__('Please enable the development mode by editing the /config/config.php file in order to reveal the error details.');
            }
            return LogUtil::registerError($returnMessage);
        }
    
        // set up all our vars with initial values
        $this->setVar('pageSize', 10);
        $this->setVar('useFeaturedResponses', false);
        $this->setVar('submissionRecipients', '');
        $this->setVar('techEmailRecipients', '');
        $this->setVar('maxFileSizeKb', 2048);
        $this->setVar('fileUploadDirectory', '');
        $this->setVar('maxPhotoWidth', 700);
        $this->setVar('maxPhotoHeight', 550);
        $this->setVar('maxThumbWidth', 120);
        $this->setVar('maxThumbHeight', 90);
        $this->setVar('useAddition1', false);
        $this->setVar('useAddition2', false);
        $this->setVar('useAddition3', false);
        $this->setVar('useAddition4', false);
        $this->setVar('useAddition5', false);
    
        $categoryRegistryIdsPerEntity = array();
    
        // create the default data
        $this->createDefaultData($categoryRegistryIdsPerEntity);
    
        // register persistent event handlers
        $this->registerPersistentEventHandlers();
    
        // register hook subscriber bundles
        HookUtil::registerSubscriberBundles($this->version->getHookSubscriberBundles());
        
    
        // initialisation successful
        return true;
    }
    
