    /**
     * Uninstall SurveyManager.
     *
     * @return boolean True on success, false otherwise.
     */
    public function uninstall()
    {
        // delete stored object workflows
        $result = Zikula_Workflow_Util::deleteWorkflowsForModule($this->getName());
        if ($result === false) {
            return LogUtil::registerError($this->__f('An error was encountered while removing stored object workflows for the %s extension.', array($this->getName())));
        }
    
        try {
            DoctrineHelper::dropSchema($this->entityManager, $this->listEntityClasses());
        } catch (\Exception $e) {
            if (System::isDevelopmentMode()) {
                return LogUtil::registerError($this->__('Doctrine Exception: ') . $e->getMessage());
            }
            return LogUtil::registerError($this->__f('An error was encountered while dropping tables for the %s extension.', array($this->name)));
        }
    
        // unregister persistent event handlers
        EventUtil::unregisterPersistentModuleHandlers($this->name);
    
        // unregister hook subscriber bundles
        HookUtil::unregisterSubscriberBundles($this->version->getHookSubscriberBundles());
        
    
        // remove all module vars
        $this->delVars();
    
        // uninstallation successful
        return true;
    }
    
    /**
     * Build array with all entity classes for SurveyManager.
     *
     * @return array list of class names.
     */
    protected function listEntityClasses()
    {
        $classNames = array();
        $classNames[] = 'SurveyManager_Entity_Survey';
        $classNames[] = 'SurveyManager_Entity_Page';
        $classNames[] = 'SurveyManager_Entity_Question';
        $classNames[] = 'SurveyManager_Entity_Response';
        $classNames[] = 'SurveyManager_Entity_ResponseData';
    
        return $classNames;
    }
    
    /**
     * Create the default data for SurveyManager.
     *
     * @param array $categoryRegistryIdsPerEntity List of category registry ids.
     *
     * @return void
     */
    protected function createDefaultData($categoryRegistryIdsPerEntity)
    {
        $entityClass = 'SurveyManager_Entity_Survey';
        $this->entityManager->getRepository($entityClass)->truncateTable();
        $entityClass = 'SurveyManager_Entity_Page';
        $this->entityManager->getRepository($entityClass)->truncateTable();
        $entityClass = 'SurveyManager_Entity_Question';
        $this->entityManager->getRepository($entityClass)->truncateTable();
        $entityClass = 'SurveyManager_Entity_Response';
        $this->entityManager->getRepository($entityClass)->truncateTable();
        $entityClass = 'SurveyManager_Entity_ResponseData';
        $this->entityManager->getRepository($entityClass)->truncateTable();
    }
    
